const u=n=>{try{return new Date(n).toLocaleDateString("ko-KR")}catch(t){return console.error("날짜 포맷팅 오류:",t),"날짜 정보 없음"}},d=n=>{try{const t=new Date(n),e=new Date().getTime()-t.getTime();if(e<0)return"방금 전";const o=Math.floor(e/(1e3*60));if(o<1)return"방금 전";if(o<60)return`${o}분 전`;const f=Math.floor(e/(1e3*60*60));if(f<24)return`${f}시간 전`;const r=Math.floor(e/(1e3*60*60*24));if(r===1)return"어제";if(r<7)return`${r}일 전`;const a=Math.floor(r/7);if(a<4)return`${a}주 전`;const s=Math.floor(r/30);return s<12?`${s}개월 전`:`${Math.floor(r/365)}년 전`}catch(t){return console.error("상대적 날짜 포맷팅 오류:",t),"날짜 정보 없음"}};export{d as a,u as f};
